```cpp
#include <iostream>
#include <vector>
#include <algorithm>
#include <cstdint>

using namespace std;

const int MOD = 1000000007;

int get_update_pair(vector<int>& costs, int index) {
    int min_cost = costs[index];
    int min_index = index;

    for (int i = index + 1; i < costs.size(); i++) {
        if (costs[i] < min_cost) {
            min_cost = costs[i];
            min_index = i;
        }
    }

    swap(costs[index], costs[min_index]);
    return min_index;
}

int max_refund(int N, vector<int>& costs) {
    int max_refund = 0;

    for (int i = 0; i < N; i++) {
        int updated_cost = costs[i] & costs[get_update_pair(costs, i)];
        max_refund = max(max_refund, updated_cost * updated_cost);
    }

    return max_refund % MOD;
}

int main() {
